import React, { useState, useEffect, useContext } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { getProfile, updateProfile } from '../../http/userAPI';
import { fetchUserReservations, cancelReservation } from '../../http/reservationAPI';
import { AuthContext } from '../../context/AuthContext';
import { FaUser, FaTicketAlt, FaCalendarAlt, FaCheck, FaTimes, FaHourglass } from 'react-icons/fa';
import './ProfilePage.css';

const ProfilePage = () => {
  const { user } = useContext(AuthContext);
  const [profile, setProfile] = useState(null);
  const [reservations, setReservations] = useState([]);
  const [activeTab, setActiveTab] = useState('profile');
  const [loading, setLoading] = useState(true);
  const [updateSuccess, setUpdateSuccess] = useState(false);
  const [cancelId, setCancelId] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const profileData = await getProfile();
        setProfile(profileData);

        const reservationsData = await fetchUserReservations();
        setReservations(reservationsData);
      } catch (error) {
        console.error('Error fetching profile data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [user]);

  const validationSchema = Yup.object({
    name: Yup.string().required('Name is required'),
    email: Yup.string().email('Invalid email address').required('Email is required')
  });

  const handleProfileUpdate = async (values, { setSubmitting }) => {
    try {
      await updateProfile(values);
      setProfile(values);
      setUpdateSuccess(true);
      setTimeout(() => setUpdateSuccess(false), 3000);
    } catch (error) {
      console.error('Error updating profile:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const handleCancelReservation = async (id) => {
    try {
      setCancelId(id);
      await cancelReservation(id);
      setReservations(reservations.map(reservation => 
        reservation.id === id 
          ? { ...reservation, status: 'CANCELLED' } 
          : reservation
      ));
    } catch (error) {
      console.error('Error cancelling reservation:', error);
    } finally {
      setCancelId(null);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'CONFIRMED':
        return <FaCheck className="status-icon confirmed" />;
      case 'CANCELLED':
        return <FaTimes className="status-icon cancelled" />;
      case 'PENDING':
      default:
        return <FaHourglass className="status-icon pending" />;
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Loading profile data...</div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="profile-header">
        <div className="container">
          <h1>My Account</h1>
        </div>
      </div>

      <div className="profile-content section">
        <div className="container">
          <div className="profile-tabs">
            <button 
              className={`tab-btn ${activeTab === 'profile' ? 'active' : ''}`}
              onClick={() => setActiveTab('profile')}
            >
              <FaUser />
              <span>Profile</span>
            </button>
            <button 
              className={`tab-btn ${activeTab === 'reservations' ? 'active' : ''}`}
              onClick={() => setActiveTab('reservations')}
            >
              <FaTicketAlt />
              <span>My Reservations</span>
            </button>
          </div>

          <div className="tab-content">
            {activeTab === 'profile' && (
              <div className="profile-tab">
                <div className="profile-card">
                  <h2>Personal Information</h2>

                  {updateSuccess && (
                    <div className="update-success">
                      Profile updated successfully!
                    </div>
                  )}

                  <Formik
                    initialValues={{
                      name: profile.name || '',
                      email: profile.email || ''
                    }}
                    validationSchema={validationSchema}
                    onSubmit={handleProfileUpdate}
                  >
                    {({ isSubmitting }) => (
                      <Form className="profile-form">
                        <div className="form-group">
                          <label htmlFor="name">Name</label>
                          <Field type="text" id="name" name="name" className="form-control" />
                          <ErrorMessage name="name" component="div" className="error-message" />
                        </div>

                        <div className="form-group">
                          <label htmlFor="email">Email</label>
                          <Field type="email" id="email" name="email" className="form-control" />
                          <ErrorMessage name="email" component="div" className="error-message" />
                        </div>

                        <button type="submit" className="btn btn-primary" disabled={isSubmitting}>
                          {isSubmitting ? 'Saving...' : 'Save Changes'}
                        </button>
                      </Form>
                    )}
                  </Formik>
                </div>
              </div>
            )}

            {activeTab === 'reservations' && (
              <div className="reservations-tab">
                <h2>My Reservations</h2>

                {reservations.length === 0 ? (
                  <div className="no-reservations">
                    <p>You don't have any reservations yet.</p>
                    <a href="/exhibitions" className="btn btn-primary">Browse Exhibitions</a>
                  </div>
                ) : (
                  <div className="reservations-list">
                    {reservations.map(reservation => (
                      <div key={reservation.id} className="reservation-card">
                        <div className="reservation-image">
                          <img 
                            src={reservation.image_url ? `${process.env.REACT_APP_API_URL}/${reservation.image_url}` : '/images/placeholder.jpg'} 
                            alt={reservation.exhibition_title} 
                          />
                        </div>
                        <div className="reservation-details">
                          <h3>{reservation.exhibition_title}</h3>
                          <div className="reservation-meta">
                            <div className="meta-item">
                              <FaCalendarAlt />
                              <span>Date: {formatDate(reservation.reservation_date)}</span>
                            </div>
                            <div className="meta-item">
                              <FaTicketAlt />
                              <span>Tickets: {reservation.visitor_count} Ã— ${reservation.ticket_price}</span>
                            </div>
                            <div className="meta-item status">
                              <span>Status: </span>
                              <span className={`status-label ${reservation.status.toLowerCase()}`}>
                                {getStatusIcon(reservation.status)}
                                {reservation.status}
                              </span>
                            </div>
                          </div>
                          <div className="reservation-actions">
                            {reservation.status !== 'CANCELLED' && (
                              <button 
                                onClick={() => handleCancelReservation(reservation.id)} 
                                className="btn btn-outline btn-cancel"
                                disabled={cancelId === reservation.id}
                              >
                                {cancelId === reservation.id ? 'Cancelling...' : 'Cancel Reservation'}
                              </button>
                            )}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
